CREATE TABLE users (  id int (10) NOT NULL AUTO_INCREMENT,  code varchar (250) NOT NULL,  ip varchar (250) NOT NULL,  useragent varchar (250) NOT NULL,  acceptlanguage varchar (250) NOT NULL,  width int(10),  height int(10),  PRIMARY KEY (id));CREATE TABLE audits (  id int (10) NOT NULL AUTO_INCREMENT,  code varchar (250) NOT NULL,  description varchar (250) NOT NULL,  PRIMARY KEY (id));CREATE TABLE observations (  id int (10) NOT NULL AUTO_INCREMENT,  identifier varchar (250) NOT NULL,  date TIMESTAMP DEFAULT CURRENT_TIMESTAMP,  element varchar (250) NOT NULL,  width int (10),  height int (10),  x int (10),  y int (10),  sx int (10),  sy int (10),  instant int (10),  type varchar (250) NOT NULL,  value BLOB,  user_id INTEGER,  audit_id INTEGER,  PRIMARY KEY (id),  FOREIGN KEY(user_id) REFERENCES users(id),  FOREIGN KEY(audit_id) REFERENCES audits(id));