CREATE TABLE users (  id INTEGER PRIMARY KEY,  code TEXT NOT NULL,  ip TEXT NOT NULL,  useragent TEXT NOT NULL,  acceptlanguage TEXT NOT NULL);CREATE TABLE audits (  id INTEGER PRIMARY KEY,  code TEXT NOT NULL,  description TEXT);CREATE TABLE observations (  id INTEGER PRIMARY KEY,  identifier TEXT NOT NULL,  date DATETIME DEFAULT CURRENT_TIMESTAMP,  element TEXT NOT NULL,  width INTEGER,  height INTEGER,  x INTEGER,  y INTEGER,  instant INTEGER,  type TEXT NOT NULL,  value BLOB NOT NULL,  user_id INTEGER,  audit_id INTEGER,  FOREIGN KEY(user_id) REFERENCES users(id),  FOREIGN KEY(audit_id) REFERENCES audits(id));